import {Link} from "react-router-dom"
import React, { useState , Component} from 'react';
import './Forms.css'

function StudentProjectForm(){
    const [name, setName] = useState("");
    const [ddl1, setDdl1] = useState("");
    const [ddl2, setDdl2] = useState("");
    const [ddl3, setDdl3] = useState("");
    const [tm1, setTM1] = useState("");
    const [tm2, setTM2] = useState("");
    const [tm3, setTM3] = useState("");

    this.state={
        name:"",
        tm1:"",
        tm2:"",
        tm3:"",
        ddl1:"",
        ddl2:"",
        ddl3:""
    }
    const handleChange = (e, {name, value}) => {
        console.log(name, value)
        this.setState({[name]: value})
      }

    const handleSubmit = (event) => {
        event.preventDefault();
        alert('You have submited the project form')
    }

    const displayForm=()=> {
        console.log('lala')
        return(
            <div>
                
            </div>
        )
    }
    return (
        <>
            <div className="container">

            <form onSubmit={handleSubmit}>
                
                <label>Project name
                    <input type="text"
                    value={name}
                    onChange={(e) => setName(e.target.value), this.handleChange}/>
                </label>
                <br/>
                <label>Team member 1
                    <input type="text"
                    value={tm2}
                    onChange={(e) => setTM1(e.target.value)}/>
                </label>
                <br/>
                <label>Team member 2
                    <input type="text"
                    value={tm2}
                    onChange={(e) => setTM2(e.target.value)}/>
                </label>
                <br/>
                <label>Team member 3
                    <input type="text"
                    value={tm3}
                    onChange={(e) => setTM3(e.target.value)}/>
                </label>
                <br/>
                <label>Deadline 1
                    <input type="text"
                    value={ddl1}
                    onChange={(e) => setDdl1(e.target.value)}/>
                </label>
                <br/>
                <label>Deadline 2
                    <input type="text"
                    value={ddl2}
                    onChange={(e) => setDdl2(e.target.value)}/>
                </label>
                <br/>
                <label>Deadline 3
                    <input type="text"
                    value={ddl3}
                    onChange={(e) => setDdl3(e.target.value)}/>
                </label>
                <br/>
                <button type="submit" onClick={displayForm}>Submit</button>
                <div>

                </div>
                
                
            </form>
            </div>
        </>
    )
    }

export default StudentProjectForm
