import React, {useState} from 'react'
import { teamsData } from './teams-data'

function TeamsView() {
    const [grade, setGrade] = useState(""); 

    const handleSubmit = (event) => {
        event.preventDefault();
        alert('You have succesfully graded the project')
    }

    const openLink = () => {
        teamsData.map((data, key) => {
            window.open(data.link);
        })
    }

    


    return (
       <>
       <div className="teams-container">
           {teamsData.map((data, key) => {
               return (
                   <>
                   <div key={key}>
                       {
                           data.teamName +
                           " , " +
                           data.tm1 +
                           " , " +
                           data.tm2 +
                           " , " +
                           data.tm3
                       }
                       <button onClick={openLink}>Open project link</button>
                   </div>
                   <div className='addGrade'>
                    
                   </div>
                    
                    </>
               )
           })}
       </div>
       <div>               <>
               <div className='add Grade'>
               <form onSubmit={handleSubmit}>
                   <label>Select team
                   <select>
                    <option value="Ford">Ford</option>
                    <option value="Volvo" selected>Volvo</option>
                    <option value="Fiat">Fiat</option>
                    </select>
                    {teamsData.map((key, value) => {
                        (<option key={key} value={value}>{value}</option>)
           })}
                   </label>
                   <label>Add Grade
                    <input type="text"
                 value={grade}
                 onChange={(e) => setGrade(e.target.value)}/>
                 </label>
                 <button type="submit">Submit</button>
                    </form>
                    </div></>
       </div>
       </>
    )
}

export default TeamsView
