import { useState } from 'react';

import React from 'react'

function Grade() {
    const [grade, setGrade] = useState(""); 

    const handleSubmit = (event) => {
        event.preventDefault();
        alert('You have succesfully graded the project')
    }

    return (
        <div className='add Grade'>
               <form onSubmit={handleSubmit}>
                   <label>Select team
                   <select>
                    <option value="Echipa 1">Echipa 1</option>
                    <option value="Echipa 2">Echipa 2</option>
                    <option value="Echipa 3">Echipa 3</option>
                    <option value="Echipa 4">Echipa 4</option>
                    <option value="Echipa 5">Echipa 5</option>
                    <option value="Echipa 6">Echipa 6</option>
                    <option value="Echipa 7">Echipa 7</option>
                    </select>
                    
                   </label>
                   <label>Add Grade
                    <input type="text"
                 value={grade}
                 onChange={(e) => setGrade(e.target.value)}/>
                 </label>
                 <button type="submit">Submit</button>
                    </form>
                    </div>
    )
}

export default Grade
